/**
 * Enum of all input types (for INPUT, BUTTON, SELECT and TEXTAREA elements)
 * specified by the W3C HTML4.01 and HTML5 specifications.
 */
export type InputType = string;
export namespace InputType {
    const BUTTON: string;
    const CHECKBOX: string;
    const COLOR: string;
    const DATE: string;
    const DATETIME: string;
    const DATETIME_LOCAL: string;
    const EMAIL: string;
    const FILE: string;
    const HIDDEN: string;
    const IMAGE: string;
    const MENU: string;
    const MONTH: string;
    const NUMBER: string;
    const PASSWORD: string;
    const RADIO: string;
    const RANGE: string;
    const RESET: string;
    const SEARCH: string;
    const SELECT_MULTIPLE: string;
    const SELECT_ONE: string;
    const SUBMIT: string;
    const TEL: string;
    const TEXT: string;
    const TEXTAREA: string;
    const TIME: string;
    const URL: string;
    const WEEK: string;
}
